ata) {\n            $data->set(\'userdata_id\', $userId);\n            $fields = $data->toArray();\n        }\n    }\n}\n\nif (!is_array($fields) || empty($fields)) {\n    $hook->addError(\'username\', \'[ExtUserRegisterPosthook] Error getting user data\');\n    return false;\n}\n\n/* Convert any nulls to \'\' */\nif (!empty($fields)) {\n    foreach ($fields as $key => $value) {\n        if (empty($value) && ($value !== \'0\')) {\n            $fields[$key] = \'\';\n        }\n    }\n    $modx->setPlaceholders($fields);\n}\n\nif ($submission) {\n    $modx->request->sanitizeRequest();\n    $dirty = false;\n\n    foreach ($fields as $key => $value) {\n        if (isset($_POST[$key])) {\n            if ($value !== $_POST[$key]) {\n                $data->set($key, $_POST[$key]);\n                $dirty = true;\n            }\n        }\n    }\n\n    if ($dirty) {\n        if ($data->save()) {\n            // $msg = \'[ExtUserRegisterPosthook] Saved successfully\';\n            // $modx->log(modX::LOG_LEVEL_ERROR, $msg);\n        } else {\n            $msg = \'[ExtUserRegisterPosthook] Save failed\';\n            $hook->addError(\'username\', $msg);\n            return false;\n        }\n    }\n}\n\nreturn true;',0,NULL,'',0,''),(21,0,0,'Cliche','Browse your albums from a single call',0,8,0,'/**\r\n * Snippet Cliche\r\n *\r\n * @package cliche\r\n */\r\n /**\r\n * Cliche\r\n *\r\n * A Gallery manager components for MODx Revolution\r\n *\r\n * @author Stephane Boulard <lossendae@gmail.com>\r\n * @package cliche\r\n */\r\n $Cliche = $modx->getService(\'cliche\',\'Cliche\',$modx->getOption(\'cliche.core_path\',null,$modx->getOption(\'core_path\').\'components/cliche/\').\'model/cliche/\',$scriptProperties);\r\nif (!($Cliche instanceof Cliche)) return \'Cliche could not be loaded\';\r\n$view = $modx->getOption(\'view\', $_REQUEST, $modx->getOption(\'view\', $scriptProperties, null));\r\n$scriptProperties[\'plugin\'] = $modx->getOption(\'plugin\', $scriptProperties, \'default\');\r\n$controllerName = ($view != null) ?  ucfirst($view) : \'Albums\';\r\n$scriptProperties[\'view\'] = strtolower($controllerName);\r\n\r\n$controller = $Cliche->loadController($controllerName);\r\n$output = $controller->run($scriptProperties);\r\nreturn $output;',0,NULL,'',0,''),(22,0,0,'ClicheAlbum','Browse a specific album',0,8,0,'/**\r\n * Snippet Cliche\r\n *\r\n * @package cliche\r\n */\r\n /**\r\n * Cliche\r\n *\r\n * A Gallery manager components for MODx Revolution\r\n *\r\n * @author Stephane Boulard <lossendae@gmail.com>\r\n * @package cliche\r\n */\r\n $Cliche = $modx->getService(\'cliche\',\'Cliche\',$modx->getOption(\'cliche.core_path\',null,$modx->getOption(\'core_path\').\'components/cliche/\').\'model/cliche/\',$scriptProperties);\r\nif (!($Cliche instanceof Cliche)) return \'Cliche could not be loaded\';\r\n$scriptProperties[\'plugin\'] = $modx->getOption(\'plugin\', $scriptProperties, \'default\');\r\n$scriptProperties[\'view\'] = \'Album\';\r\n\r\n$controller = $Cliche->loadController(\'Album\');\r\n$output = $controller->run($scriptProperties);\r\nreturn $output;',0,NULL,'',0,''),(23,0,0,'ClicheImage','View a single image',0,8,0,'/**\r\n * Snippet ClicheImage\r\n *\r\n * @package cliche\r\n */\r\n /**\r\n * Cliche\r\n *\r\n * A Gallery manager components for MODx Revolution\r\n *\r\n * @author Stephane Boulard <lossendae@gmail.com>\r\n * @package cliche\r\n */\r\n $Cliche = $modx->getService(\'cliche\',\'Cliche\',$modx->getOption(\'cliche.core_path\',null,$modx->getOption(\'core_path\').\'components/cliche/\').\'model/cliche/\',$scriptProperties);\r\nif (!($Cliche instanceof Cliche)) return \'Cliche could not be loaded\';\r\n$scriptProperties[\'plugin\'] = $modx->getOption(\'plugin\', $scriptProperties, \'default\');\r\n$scriptProperties[\'view\'] = strtolower(\'Image\');\r\n\r\n$controller = $Cliche->loadController(\'Image\');\r\n$output = $controller->run($scriptProperties);\r\nreturn $output;',0,NULL,'',0,''),(24,0,0,'fdmExportTask','FormDataManager helper snippet to export form data.',0,11,0,'/*\n * fdmExportTask \n *\n * FormDataManager\n */\n\n// Check this functionality allowed - system setting\n\n$allowed = $this->modx->getOption(\'fdm_allow_autoexports\',null,0);\nif (!$allowed) {\n    echo \"<html><title>FormDataManger Error</title><body><h1>Permission Error</h1><p>The FormDataManger auto export function is unavailable.</p></body></html>\";\n    exit();\n}\n\n// Load Class\n\nif (!isset($scriptProperties)) $scriptProperties = array();\n$fdm = $modx->getService(\'formdatamanager\', \'FormDataManager\', $modx->getOption(\'formdatamanager.core_path\', null, $modx->getOption(\'core_path\') . \'components/formdatamanager/\') . \'model/formdatamanager/\', $scriptProperties);\nif (!($fdm instanceof FormDataManager)) return \'\';\n\n$opt = (isset($fdmRunOpts)) ? trim($fdmRunOpts) : \"\";\nswitch ($opt) {\n    case \"-zip\":\n        $fdm->createZip();\n        break;\n    case \"-rar\":\n        $fdm->createRar();\n        break;\n    default:\n        \n}\n\nif (!empty($opt)) return;\n\n$processorsPath = $modx->getOption(\'core_path\', null, MODX_CORE_PATH) .\'components/formdatamanager/processors/\';\n\n$templates = array();\n\n// get all templates to process\n$classname = \'FdmLayouts\';\n$c = $modx->newQuery($classname);\n$c->select($modx->getSelectColumns($classname, $classname));\n$c->where(array(\'formtype\' => \'template\'));\n$count = $modx->getCount($classname, $c);\n$fdmdata = $modx->getCollection($classname, $c);\n\n// loop through these templates\nif (count($fdmdata)) {\n	// Format for export\n	foreach($fdmdata as $fdmd) {\n		$fd = $fdmd->toArray();\n		$templates[$fd[\'id\']] = $fd[\'formfld_extra\'];\n	}\n}\nunset($c, $fdmdata);\n\n// get all forms to process\n$classname = \'FdmLayouts\';\n$c = $modx->newQuery($classname);\n$c->select($modx->getSelectColumns($classname, $classname));\n$c->where(array(\'formtype\' => \'formit\'));\n$c->where(array(\'formtype\' => \'formz\'),xPDOQuery::SQL_OR);\n$count = $modx->getCount($classname, $c);\n$fdmdata = $modx->getCollection($classname, $c);\n\n// set folder name to use\n$savetofolder = date(\'Ymd_His\',time());\n\n// loop through these forms	\nif (count($fdmdata)) {\n	// Format for export\n	foreach($fdmdata as $fdmd) {\n		$fd = $fdmd->toArray();\n        if ($fd[\'inactive\']) continue;\n		$fields = array();\n		$fields[\'formid\'] = $fd[\'formid\'];\n		$fields[\'formname\'] = $fd[\'formname\']; \n		$fields[\'layoutid\'] = $fd[\'id\'];\n		$fields[\'template\'] = $fd[\'templateid\'];\n		$fields[\'autoexport\'] = true;\n		$lastautoexpto = $fd[\'lastautoexpto\'];\n		if (!empty($lastautoexpto)) $lastautoexpto = date(\'Y-m-d H:i:s\',strtotime($lastautoexpto) + 1);\n		$fields[\'startDate\'] = $lastautoexpto;\n		$fields[\'endDate\'] = \'\';\n		$fields[\'savetofile\'] = $fd[\'formname\'];\n	    $fields[\'savetofolder\'] = $savetofolder;\n		$response = $modx->runProcessor(\'mgr/exportdata\', $fields, array(\'processors_path\' => $processorsPath));\n	}\n}',0,NULL,'',0,''),(25,0,0,'fdmViewExportFunctions','FormDataManager helper snippet to provide extra functions when viewing/exporting form data.',0,11,0,'/*\n * fdmViewExportFunctions\n *\n * FormDataManager\n */\n\nclass FormDataManagerViewExportFunctions\n{\n \n    public function fdmfunctionlist() {\n        $afns = get_class_methods($this);\n        $fns = array();\n        foreach($afns as $fn) {\n            // get the private function names\n            if (substr($fn,0,1) == \"_\") $fns[] = substr($fn,1);\n        }\n        return $fns;\n        \n    }\n    \n    public function fdmdofunction($fn,$d) {\n        // get available functions \n        $afns = $this->fdmfunctionlist();\n        // check the requested function is in list\n        if (!in_array($fn,$afns)) return $d;\n        $w = \'_\'.$fn;\n        return ($this->$w($d));\n    }\n    \n    private function _ShowAsY($fld) {\n        if (empty($fld)) return \'N\';\n        else return \'Y\';\n    }\n    \n    private function _ShowAsYes($fld) {\n        if (empty($fld)) return \'No\';\n        else return \'Yes\';\n    }\n    \n    private function _ShowAs1($fld) {\n        if (empty($fld)) return 0;\n        else return 1;\n    }\n    \n    private function _ShowAsTrue($fld) {\n        if (empty($fld)) return \'False\';\n        else return \'True\';\n    }\n    \n    private function _ShowAsUpper($fld) {\n        if (empty($fld)) return \'\';\n        else return strtoupper($fld);\n    }\n    \n    private function _ShowAsLower($fld) {\n        if (empty($fld)) return \'\';\n        else return strtolower($fld);\n    }\n	\n	private function _ShowAsYNBlank($fld) {\n		$fld = trim($fld);\n		if (empty($fv)) return \"\";\n		$fld = strtoupper($fld);\n		switch ($fld) {\n			case \"Y\":\n			case \"YES\":\n			case \"1\":\n				return \"Y\";\n				break;\n			case \"N\":\n			case \"NO\":\n			case \"0\":\n				return \"N\";\n				break;				\n		}\n		// has content so take as positive\n		return \"Y\";\n    }\n    \n}\n\n$fdmVEF = new FormDataManagerViewExportFunctions();\nreturn $fdmVEF;',0,NULL,'',0,'');
